package com.cchtw.sfy.uitls.view.photo;import android.content.Context;import android.util.AttributeSet;import android.view.MotionEvent;/**   *  * Description: * Created by Fu.H.L on  * Date:2015-9-20-上午12:46:44 * Copyright © 2015年 Fu.H.L All rights reserved. */public class ViewPagerFixed extends android.support.v4.view.ViewPager {    public ViewPagerFixed(Context context) {        super(context);    }    public ViewPagerFixed(Context context, AttributeSet attrs) {        super(context, attrs);    }    @Override    public boolean onTouchEvent(MotionEvent ev) {        try {            return super.onTouchEvent(ev);        } catch (IllegalArgumentException ex) {            ex.printStackTrace();        }        return false;    }    @Override    public boolean onInterceptTouchEvent(MotionEvent ev) {        try {            return super.onInterceptTouchEvent(ev);        } catch (IllegalArgumentException ex) {            ex.printStackTrace();        }        return false;    }}