package com.example.shoufuyi.uitls.view.photo;import android.support.v4.view.PagerAdapter;import android.view.View;import java.util.ArrayList;/** *  * Description: * Created by Fu.H.L on  * Date:2015-9-20-上午12:43:23 * Copyright © 2015年 Fu.H.L All rights reserved. */public class MyPageAdapter extends PagerAdapter {    private ArrayList<View> listViews;    private int size;    public MyPageAdapter(ArrayList<View> listViews) {        this.listViews = listViews;        size = listViews == null ? 0 : listViews.size();    }    public void setListViews(ArrayList<View> listViews) {        this.listViews = listViews;        size = listViews == null ? 0 : listViews.size();        notifyDataSetChanged();    }    public int getCount() {        return size;    }    public int getItemPosition(Object object) {        return POSITION_NONE;    }    public void destroyItem(View arg0, int arg1, Object arg2) {        ((ViewPagerFixed) arg0).removeView(listViews.get(arg1 % size));    }    public void finishUpdate(View arg0) {    }    public Object instantiateItem(View arg0, int arg1) {        try {                ((ViewPagerFixed) arg0).addView(listViews.get(arg1 % size), 0);        } catch (Exception e) {        }        return listViews.get(arg1 % size);    }    public boolean isViewFromObject(View arg0, Object arg1) {        return arg0 == arg1;    }}